async function exportToPDF(recipe) {
  const printWindow = window.open('', '_blank');
  const imageHtml = recipe.image_url ? `<img src="${window.location.origin}${recipe.image_url}" style="width: 100%; max-height: 300px; object-fit: cover; border-radius: 12px; margin-bottom: 20px;" />` : '';
